                                header.addClass('haru-header--sticky-on');
                                // Animate Sticky
                                // var header_desktop = $('.haru-header__desktop')
                                // header_desktop.addClass('animate__hanimated1 animate__hslideInDown1')

                                $('.haru-header__sticky-holder').height(headerHeight);
